import React, { useEffect, useState } from 'react';
import Message from './Message.js';

function App() {
  const [websocket, setWebsocket] = useState(null);

  useEffect(() => {
    const ws = new WebSocket('ws://localhost:8080');

    ws.onopen = () => {
      console.log('Połączono z serwerem WebSocket.');
    };

    ws.onmessage = (event) => {
      console.log('Odebrano dane: ' + event.data);
      setReceivedTcpData(event.data);
    };

    setWebsocket(ws);

    return () => {
      ws.close();
    };
  }, []);

  const sendMessage = () => {
    if (websocket) {
      websocket.send('To jest wiadomość od klienta.');
    }
  };

  return (
    <>
      <div>
        <button onClick={sendMessage}>Wyślij wiadomość</button>
      </div>
      <Message tcpData={receivedTcpData}/>
    </>
  );
}

export default App;
